import styled from 'styled-components';

export const ContentContainer = styled.div`
    display: flex;
    justify-content: center !important;
    margin-bottom: 2rem;
`;

export const Form = styled.div`
    flex: 0 0 80%;
    max-width: 80%;
    padding: 2rem 2rem 1rem 2rem;
    border: solid 1px #ccc;
    border-radius: .25rem;
    text-align: center;
`;
