import React from 'react';
import Header from '../../components/index';
import { Container, InputGroup, FormControl, Button, Alert, Spinner } from 'react-bootstrap';
import { ContentContainer, Form } from './styles';
import ShortenerService from '../../services/shortenerService';

class HomePage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: false,
            url: '',
            code: '',
            errorMessage: '',
        }
    }

    handleSubmit = async(event) => {
        event.preventDefault();

        const { url } = this.state;

        this.setState({ isLoading: true, errorMessage: '' });

        if(!url) {
            this.setState({ isLoading: false, errorMessage: 'Informe uma url para encurtar.' });
        } else {
            try {
                const service = new ShortenerService();
                const result = await service.generate({ url });

                this.setState({ isLoading: false, code: result.code });

            } catch (err) {
                this.setState({ isLoading: false, errorMessage: 'Ops, ocorreu um erro ao tentar encurtar a url.' });
            }
        }
    }

    copyToClipboard = () => {
        const element = this.inputURL;
        element.select();
        document.execCommand('copy');
    }

    render() {
        const { isLoading, errorMessage, code } = this.state;
        return (
            <Container>
                <Header>Conheça agora Pitu, seu novo encurtador de URL</Header>
                <ContentContainer>
                    <Form onSubmit={this.handleSubmit}>
                        <InputGroup>
                            <FormControl
                                placeholder="Digite a URL para encurtar"
                                defaultValue=""
                                type="url"
                                onChange={e => this.setState({ url: e.target.value })}
                            />
                            <InputGroup.Append>
                                <Button variant="primary" type="submit">Encurtar</Button>    
                            </InputGroup.Append>
                        </InputGroup>

                        { isLoading ? (
                            <Spinner animation="border" />
                        ) : (
                            code && (
                                <>
                                <InputGroup>
                                    <FormControl
                                        autoFocus={true}
                                        defaultValue={`https://pitu.tk/${code}`}
                                        ref={(input) => this.inputURL = input}
                                    />
                                    <InputGroup.Append>
                                        <Button variant="outline-secondary" onClick={() => this.copyToClipboard()}>Copiar</Button>    
                                    </InputGroup.Append>
                                </InputGroup>
                                <p>Para acompanhar as estatísticas, acesse https://pitu.tk/{code}</p>
                                </>
                            )
                        )}
                        {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
                    </Form>
                </ContentContainer>
            </Container>
        )
    }

}

export default HomePage;
